import React from 'react';

function HourlyForecast({ data }) {
    if (!data || !data.time || !Array.isArray(data.time)) {
        return <p>No hourly forecast data available.</p>;
    }

    return (
        <div className="hourly-forecast">
            <h2>Hourly Weather Forecast</h2>
            <div className="hourly-grid">
                {data.time.map((time, index) => (
                    <div key={index} className="hourly-row">
                        <div className="hourly-time">
                            <h3>{new Date(time).toLocaleTimeString(undefined, { hour: '2-digit', minute: '2-digit' })}</h3>
                        </div>
                        <div className="hourly-details">
                            <div className="hourly-detail">
                                <p>Temperature: {data.temperature_2m[index]}Â°C</p>
                                <p>Weather Code: {data.weathercode[index]}</p>
                                <p>Wind Speed: {data.windspeed_10m[index]} km/h</p>
                                <p>Humidity: {data.relativehumidity_2m[index]}%</p>
                                <p>Cloud Cover: {data.cloudcover[index]}%</p>
                                <p>UV Index: {data.uv_index[index]}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default HourlyForecast;
