import React from 'react';

function Forecast({ data }) {
    if (!data || !data.daily) {
        return <p>No forecast data available.</p>;
    }

    const { time, temperature_2m_max, temperature_2m_min, precipitation_sum, windspeed_10m_max, windgusts_10m_max, uv_index_max } = data.daily;

    return (
        <div className="forecast">
            <h2>7-Day Weather Forecast</h2>
            <div className="forecast-grid">
                {time.map((date, index) => (
                    <div key={index} className="forecast-row">
                        <div className="forecast-date">
                            <h3>{new Date(date).toLocaleDateString(undefined, { weekday: 'long', month: 'long', day: 'numeric' })}</h3>
                        </div>
                        <div className="forecast-details">
                            <div className="forecast-detail">
                                <p>Max Temp: {temperature_2m_max[index]}°C</p>
                                <p>Min Temp: {temperature_2m_min[index]}°C</p>
                                <p>Precipitation: {precipitation_sum[index]} mm</p>
                                <p>Wind Speed: {windspeed_10m_max[index]} km/h</p>
                                <p>Wind Gusts: {windgusts_10m_max[index]} km/h</p>
                                <p>UV Index: {uv_index_max[index]}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Forecast;
